import { useState } from "react";
import api from "../api/globalApi";

function DownloadFile() {
  const [fileName, setFileName] = useState("");
  const [responseMsg, setResponseMsg] = useState("");

  const handleDownload = async () => {
    try {
      const response = await api.get(`/download/${fileName}`, {
        responseType: "blob",
      });
      if (response.status === 404 || response.data === "File not found") {
        setResponseMsg("Error: File not found");
      } else {
        const url = window.URL.createObjectURL(new Blob([response.data]));
        const link = document.createElement("a");
        link.href = url;
        link.setAttribute("download", fileName);
        document.body.appendChild(link);
        link.click();
        link.remove();
        setResponseMsg("File downloaded successfully!");
      }
    } catch (err) {
      console.error("Error downloading file:", err);
    }
  };

  return (
    <div>
      <h2>Download Generated File</h2>
      <input
        type="text"
        value={fileName}
        onChange={(e) => setFileName(e.target.value)}
        placeholder="Enter filename (e.g., app.jar)"
      />
      <button onClick={handleDownload}>Download</button>
      {responseMsg && (
        <div
          style={{
            backgroundColor: responseMsg.includes("Error")
              ? "#f44336"
              : "#4CAF50",
            color: "white",
            padding: "10px",
            borderRadius: "5px",
            marginTop: "10px",
            fontWeight: "bold",
          }}
        >
          {responseMsg}
        </div>
      )}
    </div>
  );
}

export default DownloadFile;
